    <script>
        //открытие закрытие меню
        $('.side-menu_closed').find('.side-menu__options-container-item').click(function () {
            $('.side-menu_opend').css('transform', 'translateX(0%)');
            // $('.header').css('position', 'unset');
        });
        $(document).mouseup(function (e) {
            let container = $('.side-menu_opend');
            if (container.has(e.target).length === 0) {
                $('.side-menu_opend').css('transform', 'translateX(-100%)');
                // $('.header').css('position', 'sticky');
            }
        });

        $('.side-menu_sandwich').click(function () {
            $('.side-menu_opend').css('transform', 'translateX(0%)');
            // $('.header').css('position', 'unset');
        });
    </script>
